INSERT INTO `sentences` (`id`, `content`, `bookId`, `authorId`, `type`, `length`, `createTime`, `album`) VALUES
(1612, '一件善举：一只莫基因为你与家人团聚了。', NULL, NULL, 'Game', 19, '2020-06-24 11:21:15', '精灵与萤火意志'),
(1613, '纵使长夜漫漫永劫无期，我也要化身星辰点燃希望，你的誓言由我来实现。', NULL, NULL, 'Game', 33, '2020-07-01 01:37:50', '崩坏3'),
(1614, '醒时空为梦一场，事事俱忘，何处是吾乡。', NULL, NULL, 'Game', 19, '2020-07-01 01:42:56', '崩坏3'),
(1615, '怡然独向九霄中， 坐看浮生作梦。', NULL, NULL, 'Game', 16, '2020-07-01 01:43:17', '符华'),
(1616, '纵使黑云蔽日，我也要燃烧天空，带你找到回家的路。', NULL, NULL, 'Game', 24, '2020-07-01 01:44:25', '崩坏3'),
(1617, '无论是云中落下的一滴水，还是湖面中微风吹起的一道涟漪，都有她的身影。', NULL, NULL, 'Game', 34, '2020-07-19 11:15:18', '洛克王国'),
(1618, '清晨的花朵，开在黄昏中的残骸之上，一切都会过去，不是吗？', NULL, NULL, 'Game', 28, '2020-09-06 10:01:15', '«最后一分钟»，TNO启示录事件'),
(1619, '时间的意义取决于人类：千年如一日，一日复千年。', NULL, NULL, 'Game', 23, '2020-09-06 09:44:27', '«时光飞逝»，TNO启示录事件'),
(1620, '遗忘，也是种解脱：当人们为之逝去，活下来的为之蒙恨的时候。', NULL, NULL, 'Game', 29, '2020-09-06 09:48:47', '«最后的老兵»，TNO启示录事件'),
(1621, '时代变了，但有些事情可还没变呢，大人。', NULL, NULL, 'Game', 19, '2020-09-06 09:52:16', '«无耻之徒»，TNO启示录事件'),
(1622, '仆从和主人，多么美妙的爱情故事，愿他们生活在一个能自由相爱的世界吧。', NULL, NULL, 'Game', 34, '2020-09-06 09:54:34', '核爆前半小时'),
(1623, '切尔诺贝利——人间禁地，动物的乐园。', NULL, NULL, 'Game', 18, '2020-09-06 10:48:59', '«维齐米尔的诅咒»，TNO启示录事件'),
(1624, '追求理想的利他主义，与追求道德制高点的自我满足，不仔细观察是没有区别的。', NULL, NULL, 'Game', 36, '2020-09-06 10:49:21', '«辩护»，TNO启示录事件'),
(1625, '伊甸园的存在，是为证明世界的罪恶。', NULL, NULL, 'Game', 17, '2020-09-06 10:47:21', '«动物博览园»，TNO启示录事件'),
(1626, '没错，都被我，你们的希姆莱爷爷偷走啦！', NULL, NULL, 'Game', 19, '2020-09-06 10:38:14', '«宝库»，TNO启示录事件'),
(1627, '当然是教你们养鸡啦，将优等种的德意志鸡推广到全世界！', NULL, NULL, 'Game', 26, '2020-09-06 10:38:47', '«无聊»，TNO启示录事件'),
(1628, '是我，慈祥的希姆莱爷爷哒！快进来让我摸摸。', NULL, NULL, 'Game', 21, '2020-09-06 10:39:20', '«奥斯特帕的鬼屋»，TNO启示录事件'),
(1629, '那么究竟是末日的记忆让人心冰冷，还是冰冷带来了那一天的毁灭呢？', NULL, NULL, 'Game', 31, '2020-09-06 10:40:15', '«遗迹的诅咒»，TNO启示录事件'),
(1630, '现代技术的遗产，跑车与石油，人类永恒的追求，速度与激情。燃烧与腐朽，宁愿燃烧，不愿腐朽！', NULL, NULL, 'Game', 44, '2020-09-06 10:44:00', '«最后的比赛»，TNO启示录事件'),
(1631, '诸行无常、诸法无我、涅槃喧嚣。', NULL, NULL, 'Game', 15, '2020-09-06 10:46:58', '«老和尚的寓言»，TNO启示录事件'),
(1632, '它们随风飘落，恰好落在一处光滑的斜坡上动弹不得，而后雪崩来了。', NULL, NULL, 'Game', 31, '2020-09-06 09:59:52', '«大衰退»，TNO启示录事件'),
(1633, '对于德国的奴隶，没有什么比死亡更可怕，感谢这场核战！', NULL, NULL, 'Game', 26, '2020-09-06 09:52:42', '«最后的自由»，TNO启示录事件'),
(1634, '予以民众精神上的启蒙和引导，才是真正值得歌颂的正途，愚昧的仪式只会引来更深层的愚昧。', NULL, NULL, 'Game', 42, '2020-09-06 09:56:01', '«诅咒之书»，TNO启示录事件'),
(1635, '天下大势，顺人心者兴，成功者不一定是最有能力的人，但自然是抓住机会的人。', NULL, NULL, 'Game', 36, '2020-09-06 09:57:01', '«万世为王»，TNO启示录事件'),
(1636, '有朝一日，是否会有人纪念他们。', NULL, NULL, 'Game', 15, '2020-09-06 09:59:28', '«纪念之日»，TNO启示录事件'),
(1637, '记忆，总会传承下去，无论是用何种方式。', NULL, NULL, 'Game', 19, '2020-09-06 10:00:51', '«友谊的远征»，TNO启示录事件'),
(1638, '革命家与充满血泪的时代，一起走向终结，但那不是结束，后来者还需为己而战。', NULL, NULL, 'Game', 36, '2020-09-06 09:59:02', '«红小鬼»，TNO启示录事件'),
(1639, '个人一小步，人类一大步；无论大步还是小步，总是左右世界的步伐。', NULL, NULL, 'Game', 31, '2020-09-06 09:45:34', '«人类的第二步»，TNO启示录事件'),
(1640, '当代价不再重要，人们也会对恐惧有所遗忘。', NULL, NULL, 'Game', 20, '2020-09-06 10:36:30', '«复仇»，TNO启示录事件'),
(1641, '知识是人类进步的阶梯，万代积累的结果，如果它们失传了，人类的时代也就真正落幕了。', NULL, NULL, 'Game', 40, '2020-09-06 10:15:37', '«塔罗之书»，TNO启示录事件'),
(1642, 'Heaven\'s net is wide, but there\'s nothing through.', NULL, NULL, 'Game', 50, '2020-09-15 02:23:58', '心灵终结'),
(1643, '人世几回伤往事，山形依旧枕寒流。今逢四海为家日，故垒萧萧芦荻秋。', NULL, NULL, 'Game', 32, '2020-09-06 10:19:41', '火时代传奇'),
(1644, '红，是浸满怒火的热血，是行将破晓的天空；黑，是被抛弃的蒙昧世界，是长夜将尽的洪钟。', NULL, NULL, 'Game', 41, '2020-09-06 10:21:27', '«致一代的毁灭者»，TNO启示录事件'),
(1645, '人民是健忘的，但他们总有其他方式去记住。', NULL, NULL, 'Game', 20, '2020-09-06 10:23:01', '«狂欢节»，TNO启示录事件'),
(1646, '将科学展示给原始人，他们觉得那是魔法；将魔法展示给现代人，他们总想找出些科学。', NULL, NULL, 'Game', 39, '2020-09-06 10:49:47', '«万灵药的传说»，TNO启示录事件'),
(1647, '失去记忆的遗迹只是尊空壳。', NULL, NULL, 'Game', 13, '2020-09-06 10:32:30', '«滑稽的男人»，TNO启示录事件'),
(1648, '年月把拥有变做失去，疲倦的双眼带着期望；今天只有残留的躯壳，迎接光辉岁月。', NULL, NULL, 'Game', 37, '2020-09-06 10:28:41', '«披头士出售»，TNO启示录事件'),
(1649, '物质之外亦有精神的世界。', NULL, NULL, 'Game', 12, '2020-09-06 10:15:09', '«红王公的故事»，TNO启示录事件'),
(1650, '血缘、亲情与对乡土的依恋，民族的源头，也本是最朴素无华而浪漫的东西。', NULL, NULL, 'Game', 34, '2020-09-06 10:16:01', '«不再错过»，TNO启示录事件'),
(1651, '乱世邯郸生祖龙，书文四方车轨同，青山秋草树丛从。', NULL, NULL, 'Game', 24, '2020-09-06 10:14:34', '«春秋»，TNO启示录事件'),
(1652, '最后死去的人，是最不幸的。', NULL, NULL, 'Game', 13, '2020-09-06 10:06:52', '«独面黑暗»，TNO启示录事件'),
(1653, '有安全的土地可供耕耘，有家人陪伴身旁，不必为生活担惊受怕，乱世中，平凡大众的奢望不过如此。', NULL, NULL, 'Game', 45, '2020-09-06 10:05:57', '«自由国度»，TNO启示录事件'),
(1654, '汹涌的海涛中，传来一缕希望。', NULL, NULL, 'Game', 14, '2020-09-06 10:05:32', '«永不消逝的电波»，TNO启示录事件'),
(1655, '同类间的矛盾总会生长在安逸与傲慢里。一旦这温床被打破，可笑的歧视与偏见又算得了什么呢？', NULL, NULL, 'Game', 43, '2020-09-06 10:03:02', '«可被触碰者»，TNO启示录事件'),
(1656, '离别长，欢聚短，若共把酒为歌，且斟余生一酌。', NULL, NULL, 'Game', 22, '2020-10-20 04:09:12', '烟雨江湖'),
(1657, '在黑暗中，我们看到光明。', NULL, NULL, 'Game', 12, '2020-11-02 09:46:37', '明日方舟'),
(1658, '我回来，是为了那些回不来的人。', NULL, NULL, 'Game', 15, '2020-11-08 01:24:07', '英雄联盟'),
