INSERT INTO `sentences` (`id`, `content`, `bookId`, `authorId`, `type`, `length`, `createTime`, `album`) VALUES
(3122, '她终于看清了，使自己这粒沙尘四处飘飞的，是怎样的天风；把自己这片小叶送向远方的，是怎样的大河。', NULL, NULL, 'Literature', 47, '2022-03-31 08:48:42', '三体3：死神永生'),
(3123, '在这样的世界中，你从爱人的怀抱中起身，走出几步，就与他隔开千万年。', NULL, NULL, 'Literature', 33, '2022-03-31 08:50:49', '三体3'),
(3124, '从来如此，便对吗？', NULL, NULL, 'Literature', 9, '2022-04-02 12:41:47', '呐喊·狂人日记'),
(3125, '你还很年轻，将来你会遇到很多人，经历很多事，得到很多，也会失去很多，但无论如何，有两样东西，你绝不能丢弃，一个叫良心，另一个叫理想。', NULL, NULL, 'Literature', 66, '2022-04-11 08:35:40', '明朝那些事儿'),
(3126, '强而不欺，威而不霸，这才是一个伟大国家和民族的气度与底蕴。', NULL, NULL, 'Literature', 29, '2022-04-11 08:35:54', '明朝那些事儿'),
(3127, '一个人要显示自己的力量，从来不是靠暴力，挑战这一准则的人必然会被历史从强者的行列中淘汰，历来如此。', NULL, NULL, 'Literature', 49, '2022-04-11 08:36:31', '明朝那些事儿'),
(3128, '知道可能面对的困难和痛苦，在死亡的恐惧中不断挣扎，而仍然能战胜自己，选择这条道路，才是真正的勇气。', NULL, NULL, 'Literature', 49, '2022-04-11 08:37:09', '明朝那些事儿'),
(3129, '只有真正了解这个世界的丑陋与污浊，被现实打击，被痛苦折磨，遍体鳞伤、无所遁形，却从未放弃对光明的追寻，依然微笑着，坚定前行的人，才是真正的勇者。', NULL, NULL, 'Literature', 72, '2022-04-11 08:37:35', '明朝那些事儿'),
(3130, '读书的能力和处理问题的能力是不一样的。书读得好，不代表事情能处理得好；能列出计划，不代表能够执行计划。', NULL, NULL, 'Literature', 51, '2022-04-11 08:38:24', '明朝那些事儿'),
(3131, '飞鸟不留痕，但我已飞过。', NULL, NULL, 'Literature', 12, '2022-04-16 09:03:25', '泰戈尔诗集'),
(3132, '适有孤鹤，横江东来。翅如车轮，玄裳缟衣，戛然长鸣，掠予舟而西也。', NULL, NULL, 'Literature', 32, '2021-05-14 11:34:42', '后赤壁赋'),
(3133, '给每一条河每一座山取一个温暖的名字。陌生人，我也为你祝福。', NULL, NULL, 'Literature', 29, '2021-05-14 11:45:15', '面朝大海，春暖花开'),
(3134, '我是天空里的一片云，偶尔投影在你的波心。', NULL, NULL, 'Literature', 20, '2021-05-14 11:52:21', '偶然'),
(3135, '凡是过去，皆为序章；凡是未来，皆有可期。', NULL, NULL, 'Literature', 20, '2021-05-14 11:57:27', '暴风雨'),
(3136, '离歌且莫翻新阕，一曲能教肠寸结。直须看尽洛城花，始共春风容易别。', NULL, NULL, 'Literature', 32, '2022-07-16 09:51:29', '玉楼春·尊前拟把归期说'),
(3137, '自童年起，我便独自一人，照顾着历代星辰。', NULL, NULL, 'Literature', 20, '2022-07-16 03:53:43', '孤独'),
(3138, '月色与雪色之间，你是第三种绝色。', NULL, NULL, 'Literature', 16, '2022-07-16 03:51:40', '绝色'),
(3139, '月光还是少年的月光，九州一色还是李白的霜。', NULL, NULL, 'Literature', 21, '2022-07-16 03:43:07', '独白'),
(3140, '世间安得双全法， 不负如来不负卿。', NULL, NULL, 'Literature', 17, '2022-07-16 03:38:44', '仓央嘉措诗歌全集'),
(3141, '少年不惧岁月长，彼方尚有荣光在。', NULL, NULL, 'Literature', 16, '2022-07-16 03:41:07', '暗格里的秘密'),
(3142, '自童年起，我便独自一人，照顾着历代星辰。', NULL, NULL, 'Literature', 20, '2022-07-16 08:01:45', '独白'),
(3143, '最聪明的处世术是，既对世俗投以白眼，又与其同流合污。', NULL, NULL, 'Literature', 26, '2022-07-14 07:34:32', '河童'),
(3144, '这里荒芜寸草不生，后来你来这走了一遭，奇迹般万物生长，这里是我的心。', NULL, NULL, 'Literature', 34, '2022-07-11 07:38:31', '沙漠'),
(3145, '心之何如，有似万丈迷津，遥亘千里，其中并无舟子可以渡人，除了自渡，他人爱莫能助。', NULL, NULL, 'Literature', 40, '2022-07-10 09:47:28', '不负我心'),
(3146, '院内的枣树不知怎的，今年竟也结了几颗果实，不好，略显苦涩，但转念一想昨晚梦中的那位女子，倒也甜了几分。', NULL, NULL, 'Literature', 51, '2022-07-07 01:50:41', '鲁迅文集'),
(3147, '所爱隔山海，山海皆可平。', NULL, NULL, 'Literature', 12, '2022-06-19 07:18:30', '山木诗词'),
